import React, { Component } from "react";

export default class Counter extends Component {
  state = {
    count: 5,
    tags: ["tag1", "tag2", "tag3"],
  };

  styles = {
    fontWeight: "bold",
    background: "black",
    color: "white",
  };

  formatCount = () => {
    const count = this.state.count;
    return count === 0 ? "Zero" : count;
  };

  backgroundChange = () => {
    let theClass = "m-2 btn btn-";
    theClass += this.state.count === 0 ? "danger" : "warning";
    return theClass;
  };

  
  renderTags = (tagsArray) => {
    if (tagsArray.length === 0) return "We have empty Array";
    return (
      <ul>
        {tagsArray.map((tag) => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  };

  handleIncrement = (incrementCounter) => {
    this.setState({ count: this.state.count + incrementCounter });
  };

  render() {
    return (
      <React.Fragment>
        <span className={this.backgroundChange}>{this.formatCount}</span>
        <button
          onClick={() => this.handleIncrement(-1)}
          className="btn btn-primary btn-sm m-2"
        >
          MINUS
        </button>
        <button
          onClick={() => this.handleIncrement(1)}
          className="btn btn-primary btn-sm m-3"
        >
          PLUS
        </button>
        {this.renderTags(this.state.tags)}
      </React.Fragment>
    );
  }
}
