import React, { Component } from "react";

export default class Counter extends Component {
    state = {
        count: 0,
    };
    
    styles = {
        fontWeight: 'bold',
        background: 'black',
        color: 'white'
    }
  
    render() {    
        return (
            <React.Fragment>
                <span style={this.styles} className="btn m-2">{this.formatCount()}</span>
                <button className="btn btn-primary btn-sm">click here</button>
            </React.Fragment>
        );
    }

    formatCount() { 
        const { count } = this.state;
        if (count === 0) {
            this.styles.background = 'yellow'
            this.styles.color = ''
            return 'zero';
        }
        else { 
            this.styles.background = 'red'
            return count;
        }
            
    }

}
