import React, { Component } from "react";

export default class Counter extends Component {
    state = {
        count: 5,
    };
    

  
    render() {    
        return (
            <React.Fragment>
                <span style={{fontWeight: 'bold',background: 'black', color: 'white'}} className="btn m-2">{this.formatCount()}</span>
                <button className="btn btn-primary btn-sm">click here</button>
            </React.Fragment>
        );
    }

    formatCount() { 
        const { count } = this.state;
        return count === 0 ? 'zero' : count;
    }

}
