import React, { Component } from "react";

export default class Counter extends Component {
  state = {
    count: 10,
    tags: ['tag1','tag3'],
  };

  styles = {
    fontWeight: "bold",
    background: "black",
    color: "white",
  };

  formatCount(count) {
    if (count === 0) {
      this.styles.background = "red";
      return "zero";
    }
    this.styles.background = "black";
    return count;
  }
  renderTags(tagsArray) {
    if (tagsArray.length == 0) return "We have empty Array";
    return (
      <ul>
        {tagsArray.map((tag) => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }

  render() {
    return (
      <React.Fragment>
        <span style={this.styles} className="btn m-2">
          {this.formatCount(this.state.count)}
        </span>
        <button className="btn btn-primary btn-sm">click here</button>
        {this.renderTags(this.state.tags)}
      </React.Fragment>
    );
  }
}
