import React, { Component } from "react";

export default class Counter extends Component {
  state = {
    count: 5,
    tags: ["tag1", "tag2", "tag3"],
  };

  styles = {
    fontWeight: "bold",
    background: "black",
    color: "white",
  };

  formatCount() {
    const count = this.state.count;
    return count === 0 ? "Zero" : count;
  }

    backgroundChange() {
        let theClass = 'btn-';
        theClass += this.state.count === 0 ? 'primary' : 'secon';
        return theClass;
  }
  renderTags(tagsArray) {
    if (tagsArray.length === 0) return "We have empty Array";
    return (
      <ul>
        {tagsArray.map((tag) => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }

  handleIncrement = () => {
    this.setState({ count: this.state.count - 1 });
  };

  render() {
    return (
      <React.Fragment>
        <span style={this.backgroundChange()} className="btn m-2">
          {this.formatCount()}
        </span>
        <button
          onClick={this.handleIncrement}
          className="btn btn-primary btn-sm"
        >
          click here
        </button>
        {this.renderTags(this.state.tags)}
      </React.Fragment>
    );
  }
}
