import React, { Component } from "react";

export default class Counter extends Component {
    state = {
        count: 500,
        tags: ['tag1','tag2','tag3']
    };
    
    styles = {
        fontWeight: 'bold',
        background: 'black',
        color: 'white'
    }
  
    render() {    
        return (
            <React.Fragment>
                <span style={this.styles} className="btn m-2">{this.formatCount()}</span>
                <button className="btn btn-primary btn-sm">click here</button>
                <ul>{ this.state.tags.map(tag => <li key={tag}>{ tag }</li>) }</ul>
            </React.Fragment>
        );
    }

    formatCount() { 
        const { count } = this.state;
        if (count === 0) {
            this.styles.background = 'red'
            return 'zero';
        }
        else { 
            this.styles.background = 'black'
            return count;
        }            
    }
}
